		?>	</div>		 <script type="text/javascript" > 	 	function DJCFRegionsSelect<?php echo $module->id;?>(id){	 		var exdate=new Date();		  	exdate.setDate(exdate.getDate() + 30);		  	document.cookie = "djcf_regid=" + id + "; path=/; expires=" + exdate.toUTCString();		  	location.reload(); 		  		 	}	 </script>	 <?php if(!$ulatlon){ ?>	 	<script type="text/javascript" >	 				function getmodREGSELECTDJLocation<?php echo $module->id;?>(){					document.id('user_pos<?php echo $module->id;?>').addEvent('click',function(event){										if(navigator.geolocation){						navigator.geolocation.getCurrentPosition(modREGSELECTDJPosition<?php echo $module->id;?>);					}				})			}			 			function modREGSELECTDJPosition<?php echo $module->id;?>(position){			  	var exdate=new Date();			  	exdate.setDate(exdate.getDate() + 30);				var ll = position.coords.latitude+'_'+position.coords.longitude;			  	document.cookie = "djcf_clatlon=" + ll + "; path=/; expires=" + exdate.toUTCString();		  		location.reload(); 		  	} 	 	</script>		 <?php } ?>